mapper.select:
select  bmno, bmemail, bmname, bmtel, bmgrade
from branch_master
limit #{startIndex}, #{pageSize}

select  bmemail, bmname, bmtel
from branch_master
where bmno = #{branchmasterNo}

mapper.update:
update branch_master set 
  bmname = #{name}, 
  bmtel = #{tel},
  bmemail = #{email}
where bmemail=#{email}
